import React from "react";
import { useState, useEffect } from "react";
import { Switch, Route, Link } from "react-router-dom";
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import * as AuthService from "./services/auth.service";
import IUser from './types/user.type';
import Login from "./components/Login";
import Register from "./components/Register";
import Home from "./components/Home";
import Profile from "./components/Profile";
import EventBus from "./common/EventBus";
import {Container, Nav, Navbar, NavDropdown} from 'react-bootstrap';

const App: React.FC = () => {

  const [currentUser, setCurrentUser] = useState<IUser | undefined>(undefined);

  useEffect(() => {
    const user = AuthService.getCurrentUser();
    if (user) {
      setCurrentUser(user);
    }
    EventBus.on("logout", logOut);
    return () => {
      EventBus.remove("logout", logOut);
    };
  }, []);
  const logOut = () => {
    AuthService.logout();
    setCurrentUser(undefined);
  };

  return (
    <div>
      {currentUser ? (
        <Navbar>
            <div className="nav-custom">
              <div>
                <Navbar.Brand href="#home" style={{paddingTop:5}}>
                  <span className="nav-title">
                  Biro Perjalanan
                  </span>
                </Navbar.Brand>
              </div>
              <div className="nav-right">
                <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                <Navbar.Collapse id="responsive-navbar-nav">
                  <Nav className="me-auto">
                    <NavDropdown title="Profile" 
                                id="collasible-nav-dropdown"
                                className="nav-drop">
                      <NavDropdown.Item href="/profile">Profile</NavDropdown.Item>
                      <NavDropdown.Item href="/login" onClick={logOut}>Log Out</NavDropdown.Item>
                    </NavDropdown>
                  </Nav>
                </Navbar.Collapse>
              </div>
            </div>
        </Navbar>
        // <nav className="navbar navbar-expand">
        //   <div className="navbar-nav mr-auto">
        //     <li className="nav-item">
        //         <Link to={"/profile"} className="nav-link">
        //           Profile
        //         </Link>
        //     </li>
        //     <li className="nav-item">
        //         <a href="/login" className="nav-link" onClick={logOut}>
        //           Logout
        //         </a>
        //     </li>
        //   </div>
        // </nav>
      ):(
        <div></div>
      )}
      <div className="container mt-3">
        <Switch>
          <Route exact path={["/", "/login"]} component={currentUser? Profile : Login} />
          <Route exact path="/register" component={Register} />
          <Route exact path="/home" component={currentUser? Home : Login} />
          <Route exact path="/profile" component={currentUser? Profile : Login} />
        </Switch>
      </div>
    </div>
  );
};
export default App;
